import {Card} from 'react-bootstrap'; 

const Test = () => {
  const [htmlText, setHtmlText] = useState("");
  const [url1, setUrl1] = useState("");
  const [url2, setUrl2] = useState("");

  useEffect(() => { // get the html from backend
    console.log("inside test");
    axios.get("http://localhost:4000/getsavedpage")
      .then((response) => {
        setHtmlText(response.data);
      })
      .catch(() => {
        console.log('failed to get HTML string / useEffect');
      });
  }, []);


  return (
    <div>


      <Card className="URL-Card">
        <div    >
          <label for="exampleFormControlInput1">First URL</label>
          <input type="email" class="form-control" id="exampleFormControlInput1" placeholder="name@example.com" />
        </div>
    </Card>    
      <Card className="URL-Card">
        <div  >
        <label for="exampleFormControlInput1">Second URL</label>
        <input type="email" class="form-control" id="exampleFormControlInput1" placeholder="name@example.com" />
      </div>
    </Card>





      {<div dangerouslySetInnerHTML={{ __html: htmlText }} />}
    </div>
  )
};

export default Test;
